(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let r=null;const s=t=>{const{hours:s,minutes:a,seconds:l}=(t=>{let e=(new Date(t).getTime()-(new Date).getTime())/1e3;return{hours:Math.floor(e/60/60),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})(t);+s+ +a+ +l<0?clearInterval(r):(e.textContent=String(s).padStart(2,"0"),n.textContent=String(a).padStart(2,"0"),o.textContent=String(l).padStart(2,"0"))};s(t),r=setInterval(s,1e3,t)})("31 july 2022")})();